import { List } from 'immutable';

class Enqueue {
  /**
   * Reduce the state
   */
  execute(state, tracks) {
    if (tracks) {
      let localTracks = tracks;
      if (!List.isList(localTracks)) {
        localTracks = List.of(localTracks);
      }
      return state.set(
        'queue',
        state.get('queue').concat(localTracks),
      );
    } else if (state.get('tracks').size > 0) {
      return state.set(
        'queue',
        state.get('queue').push(state.getIn(['tracks', 0])),
      );
    }

    return null;
  }
}

export default new Enqueue();

import Sound from 'react-sound';

class Next {
  /**
   * Reduce the state
   */
  execute(state) {
    const currentTrack = state.get('queue').get(0);

    if (state.get('queue').size === 1) {
      return state.setIn(['playback', 'playStatus'], Sound.status.STOPPED);
    }

    return state.set(
      'queue',
      state.get('queue').shift(),
    ).set(
      'history',
      state.get('history').push(currentTrack),
    );
  }
}

export default new Next();

import Sound from 'react-sound';

class Play {
  /**
   * Reduce the state
   */
  execute(state) {
    if (state.get('queue').size > 0) {
      return state.setIn(['playback', 'playStatus'], Sound.status.PLAYING);
    }

    return state;
  }
}

export default new Play();

class Playing {
  /**
   * Reduce the state
   */
  execute(state, audioStatus) {
    return state.setIn(['playback', 'elapsed'], audioStatus.elapsed)
      .setIn(['playback', 'total'], audioStatus.total)
      .setIn(['playback', 'position'], audioStatus.position);
  }
}

export default new Playing();

class Prev {
  /**
   * Reduce the state
   */
  execute(state) {
    if (state.get('history').size > 0) {
      const prevTrack = state.get('history').peek();

      return state.set('history',
        state.get('history').shift()).set('queue',
        state.get('queue').insert(0, prevTrack));
    }

    return state;
  }
}

export default new Prev();

